var util = require('util');
var SerialPort = require('serialport').SerialPort;
var xbee_api = require('../../lib/xbee-api.js');

var C = xbee_api.constants;

var xbeeAPI = new xbee_api.XBeeAPI({
  api_mode: 1
});

var serialport = new SerialPort("/dev/ttyUSB1", {
  baudrate: 9600,
  parser: xbeeAPI.rawParser()
});

serialport.on("data",function(data){
    console.log("data ",data.toString("HEX"));
})
serialport.on("open", function() {
  console.log("Serial port open... sending ATND");
  var frame ={
      type: 0x00, // xbee_api.constants.FRAME_TYPE.TX_REQUEST_64
      id: 0x01, // optional, nextFrameId() is called per default
      destination64: "13A200406FB3A1",
      options: 0x00, // optional, 0x00 is default
      data: "TxData0A" // Can either be string or byte array.
  };

  serialport.write(xbeeAPI.buildFrame(frame), function(err, res) {
    if (err) throw(err);
    else     console.log("written bytes: "+util.inspect(res));
  });
});


xbeeAPI.on("frame_object", function(frame) {
  console.log("OBJ> "+util.inspect(frame));
});
